default.all_types:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT DISTINCT ?type
WHERE {
    ?s rdf:type ?type .
}
ORDER BY ?type

default.type_properties:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT ?type ?property (COUNT(?property) AS ?propertyCount)
WHERE {
    ?s rdf:type ?type ;
       ?property ?o .
}
GROUP BY ?type ?property
ORDER BY ?type DESC(?propertyCount)

default.all_properties:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT DISTINCT ?property
WHERE {
    ?s ?property ?o .
}
ORDER BY ?property

default.subjects_with_type:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT DISTINCT ?subject
WHERE {
    ?subject rdf:type <TYPE_URI> .
}
ORDER BY ?subject

default.subjects_with_type_and_property:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT DISTINCT ?subject
WHERE {
    ?subject rdf:type <TYPE_URI> ;
             ?property ?o .
    FILTER(?property = <PROPERTY_URI>)
}
ORDER BY ?subject

default.subjects_with_type_and_property_value:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT DISTINCT ?subject
WHERE {
    ?subject rdf:type <TYPE_URI> ;
             ?property <VALUE_URI> .
    FILTER(?property = <PROPERTY_URI>)
}
ORDER BY ?subject
