filter_resources_by_location:
SELECT DISTINCT ?resource ?resourceType ?locationLabel WHERE {
  ?resource cto:elementOf n4c:E5320 .
  ?resource cto:relatedLocation ?location .
  ?location rdfs:label ?locationLabel .
  FILTER(CONTAINS(LCASE(STR(?locationLabel)), "dresden")) .
  OPTIONAL { ?resource rdf:type ?resourceType . }
}

filter_concerts_by_date_location:
SELECT ?name ?date WHERE {
  ?concert rdfs:label ?name .
  ?concert cto:elementOf n4c:E5320 .
  ?concert cto:relatedLocation ?location .
  OPTIONAL {?location rdfs:label ?locationName . }
  FILTER(CONTAINS(?locationName, "Dresden")) .
  FILTER(REGEX(?name, "\\b\\d{1,2}\\.\\d{1,2}\\.\\d{4}\\b")) .
  BIND(REPLACE(?name, ".*?(\\d{1,2}\\.\\d{1,2}\\.\\d{4}).*", "$1") AS ?date)
}

filter_elements_related_to_dresden:
SELECT DISTINCT * WHERE {
  ?something cto:elementOf ?id .
  ?something cto:relatedLocation ?location .
  OPTIONAL {?something rdfs:label ?name .}
  ?location rdfs:label ?locationName .
  FILTER(CONTAINS(?locationName, "Dresden")) .
} ORDER BY ?name
